import React from "react";
import Adapted from "../../Assets/SVGs/Adapted.svg";
import Anchor from "../../Assets/SVGs/Anchor.svg";
import Authentic from "../../Assets/SVGs/Authentic.svg";
import Glossaries from "../../Assets/SVGs/Glossaries.svg";
import Letters from "../../Assets/SVGs/Letters.svg";
import MultilingualSummaries from "../../Assets/SVGs/multilingual_summaries.svg";
import Notice from "../../Assets/SVGs/Notice.svg";
import Text from "../../Assets/SVGs/text_sketch.svg";
import { motion, AnimatePresence } from "framer-motion";
import { faXmark } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import "../../Styles/Modals.css";

export const Modals = ({
  anchorCharts,
  setAnchorCharts,
  multiGlossaries,
  setMultiGlossaries,
  notice,
  setNotice,
  multiParent,
  setMultiParent,
  multiSummaries,
  setMultiSummaries,
  authentic,
  setAuthentic,
  summaries,
  setSummaries,
  textSketch,
  setTextSketch,
}) => {
  const backdrop = {
    visible: { opacity: 1 },
    hidden: { opacity: 0 },
  };

  const modal = {
    hidden: {
      y: "-100vh",
      opacity: 0,
    },
    visible: {
      y: "0px",
      opacity: 1,
      transition: { delay: 0.5 },
    },
  };

  const handleClose = () => {
    setAnchorCharts(false);
    setAuthentic(false);
    setMultiGlossaries(false);
    setMultiParent(false);
    setMultiSummaries(false);
    setNotice(false);
    setSummaries(false);
    setTextSketch(false);
  };

  const setImage = () => {
    if (anchorCharts) {
      return Anchor;
    } else if (multiGlossaries) {
      return Glossaries;
    } else if (notice) {
      return Notice;
    } else if (multiParent) {
      return Letters;
    } else if (multiSummaries) {
      return MultilingualSummaries;
    } else if (authentic) {
      return Authentic;
    } else if (summaries) {
      return Adapted;
    }
  };

  return (
    <>
      <AnimatePresence exitBeforeEnter>
        {anchorCharts ||
        multiGlossaries ||
        notice ||
        multiParent ||
        multiSummaries ||
        authentic ||
        summaries ||
        textSketch ? (
          <motion.div
            className={`outerModal flex centerFlex}`}
            variants={backdrop}
            initial="hidden"
            animate="visible"
            exit="hidden"
          >
            <motion.div
              className={`flex flexColumn innerModal`}
              variants={modal}
              initial="hidden"
              animate="visible"
              exit="hidden"
            >
              <div className="whole-modal">
                <div className="button-row">
                  <button className="close-button" onClick={handleClose}>
                    <FontAwesomeIcon className="x-icon free" icon={faXmark} />
                  </button>
                </div>
                <div className="main-modal">
                  <img className="modal-pic" alt="modal pic" src={setImage()} />
                </div>
              </div>
            </motion.div>
          </motion.div>
        ) : null}
      </AnimatePresence>
    </>
  );
};

export default Modals;
